                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    $included = in_array($moduleName, $modules);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    if ($included) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        $checkedModules[] = [
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            'name' => $moduleName,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            'icon' => $moduleData['icon'],
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            'included' => true,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        ];
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    } else {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        $uncheckedModules[] = [
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            'name' => $moduleName,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            'icon' => $moduleData['icon'],
